declare
   too_young_ex exception;
   too_young_const constant number := -20000;
   pragma exception_init (too_young_ex, -20000);
   age integer := 16;
begin
   if age < 18 then
      raise_application_error (too_young_const, 'Vous Ãªtes trop jeune.');
   end if;
exception when too_young_ex then
      dbms_output.put_line ('SQLCODE: ' || sqlcode || ', SQLERRM: ' || sqlerrm);
      dbms_output.put_line (dbms_utility.format_error_stack);
end;
